const asciiCache = new Map();
const asciiRamp = [
  "@",
  "W",
  "M",
  "B",
  "8",
  "&",
  "%", 
  "#",
  "*",
  "+",
  "=",
  "-",
  ":",
  ".",
  " "
];

const init = () => {
  const existingSvg = container.querySelector("svg");
  if (existingSvg) {
    existingSvg.remove();
  }
  const width = container.clientWidth;
  const height = Math.max(520, container.clientHeight);
  layoutSize = { width, height };

  svg = d3
    .select(container)
    .append("svg")
    .attr("viewBox", `0 0 ${width} ${height}`)
    .attr("role", "img")
    .attr("aria-label", "Evolution tree visualization");

  g = svg.append("g");

  root = d3.hierarchy(data);
  const treeLayout = d3.tree().size([height - margin.top - margin.bottom, width]);
  treeLayout(root);

  const maxMya = d3.max(root.descendants(), (d) => d.data.mya);
  const minMya = d3.min(root.descendants(), (d) => d.data.mya);
  const timeScale = d3
    .scaleLinear()
    .domain([maxMya, minMya])
    .range([margin.left, width - margin.right]);

  nodes = root.descendants();
  nodes.forEach((node, index) => {
    node.y = timeScale(node.data.mya);
    node.x = node.x + margin.top;
    node._index = index;
  });

  const linkGenerator = d3
    .linkHorizontal()
    .x((d) => d.y)
    .y((d) => d.x);

  linkGroup = g.append("g")
    .selectAll("path")
    .data(root.links())
    .join("path")
    .attr("d", linkGenerator)
    .attr("fill", "none")
    .attr("stroke", "#334155")
    .attr("stroke-width", 2);

  nodeGroup = g.append("g").selectAll("g").data(nodes).join("g");

  nodeGroup
    .append("circle")
    .attr("cx", (d) => d.y)
    .attr("cy", (d) => d.x)
    .attr("r", (d) => (d.depth === 0 ? nodeRadius + 2 : nodeRadius))
    .attr("class", "node-circle")
    .attr("fill", (d) => {
      if (d.depth === 0) return "#f8fafc";
      if (d.depth <= 2) return "#38bdf8";
      return "#94a3b8";
    })
    .attr("stroke", "#ffffff")
    .attr("stroke-width", 2)
    .on("mouseenter", (event, d) => showTooltip(event, d))
    .on("mousemove", (event) => moveTooltip(event))
    .on("mouseleave", hideTooltip)
    .on("click", (event, d) => {
      setActiveNode(d._index, true);
      showTooltip(event, d);
    })
    .on("touchstart", (event, d) => {
      event.preventDefault();
      setActiveNode(d._index, true);
      showTooltip(event, d);
    });

  nodeGroup
    .append("text")
    .attr("x", (d) => d.y + 12)
    .attr("y", (d) => d.x + 4)
    .text((d) => d.data.name.toUpperCase())
    .attr("font-size", 12)
    .attr("fill", "#e2e8f0");

  const axis = d3.axisBottom(timeScale).ticks(6).tickFormat((d) => `${d} mya`);
  g.append("g")
    .attr("transform", `translate(0, ${height - margin.bottom})`)
    .call(axis)
    .call((selection) => selection.select(".domain").attr("stroke", "#475569"))
    .call((selection) => selection.selectAll("text").attr("fill", "#94a3b8"));

  zoomBehavior = d3
    .zoom()
    .scaleExtent([0.6, 2.8])
    .on("zoom", (event) => {
      g.attr("transform", event.transform);
    });

  svg.call(zoomBehavior);
  resetButton.addEventListener("click", () => {
    svg.transition().duration(400).call(zoomBehavior.transform, d3.zoomIdentity);
  });

  const savedIndex = nodes.findIndex((node) => node.data.name === activeNodeName);
  revealedCount = Math.max(1, Math.min(revealedCount, nodes.length));
  setActiveNode(savedIndex >= 0 ? savedIndex : 0, false);
};

const setActiveNode = (index, animate) => {
  if (!nodes.length) return;
  activeIndex = Math.min(Math.max(index, 0), revealedCount - 1);
  const node = nodes[activeIndex];
  activeNodeName = node.data.name;
  currentNodeLabel.textContent = node.data.name.toUpperCase();
  setAsciiForNode(node.data.name);
  prevButton.disabled = activeIndex === 0;
  nextButton.disabled = revealedCount === nodes.length && activeIndex === nodes.length - 1;

  updateVisibility();

  nodeGroup
    .selectAll("circle")
    .classed("node-active", (d) => d._index === activeIndex);

  const ancestorSet = new Set(node.ancestors());
  linkGroup.classed("link-active", (link) => ancestorSet.has(link.target));

  if (animate) {
    focusNode(node);
  }
};

const getAsciiDimensions = () => {
  const columns = Math.max(70, Math.floor(container.clientWidth / 6));
  const rows = Math.max(26, Math.floor(container.clientHeight / 12));
  return { columns, rows };
};

const toAscii = (image, columns, rows) => {
  const canvas = document.createElement("canvas");
  canvas.width = columns;
  canvas.height = rows;
  const ctx = canvas.getContext("2d", { willReadFrequently: true });
  ctx.drawImage(image, 0, 0, columns, rows);
  const { data } = ctx.getImageData(0, 0, columns, rows);
  let output = "";

  for (let row = 0; row < rows; row += 1) {
    let line = "";
    for (let col = 0; col < columns; col += 1) {
      const i = (row * columns + col) * 4;
      const r = data[i];
      const g = data[i + 1];
      const b = data[i + 2];
      const alpha = data[i + 3] / 255;
      const brightness = (0.2126 * r + 0.7152 * g + 0.0722 * b) * alpha;
      const rampIndex = Math.floor(
        (brightness / 255) * (asciiRamp.length - 1)
      );
      line += asciiRamp[rampIndex];
    }
    output += `${line}\n`;
  }

  return output;
};

const setAsciiForNode = (nodeName) => {
  const { columns, rows } = getAsciiDimensions();
  const cacheKey = `${nodeName}-${columns}x${rows}`;
  const source = imageSources[nodeName];

  if (asciiFileSources[nodeName]) {
    const filePath = asciiFileSources[nodeName];
    if (asciiFileCache.has(filePath)) {
      asciiBackground.textContent = asciiFileCache.get(filePath);
      return;
    }

    asciiBackground.textContent = "LOADING ASCII...";
    fetch(filePath)
      .then((response) => (response.ok ? response.text() : Promise.reject(response)))
      .then((text) => {
        asciiFileCache.set(filePath, text);
        asciiBackground.textContent = text;
      })
      .catch(() => {
        asciiBackground.textContent = asciiArt[nodeName] || asciiArt.Animals;
      });
    return;
  }

  if (asciiCache.has(cacheKey)) {
    asciiBackground.textContent = asciiCache.get(cacheKey);
    return;
  }

  if (!source) {
    asciiBackground.textContent = asciiArt[nodeName] || asciiArt.Animals;
    return;
  }

  asciiBackground.textContent = "LOADING IMAGE...";
  const requestId = ++asciiRequestId;
  const image = new Image();
  image.crossOrigin = "anonymous";
  image.onload = () => {
    if (requestId !== asciiRequestId) return;
    try {
      const ascii = toAscii(image, columns, rows);
      asciiCache.set(cacheKey, ascii);
      asciiBackground.textContent = ascii;
    } catch (error) {
      asciiBackground.textContent = asciiArt[nodeName] || asciiArt.Animals;
    }
  };
  image.onerror = () => {
    if (requestId !== asciiRequestId) return;
    asciiBackground.textContent = asciiArt[nodeName] || asciiArt.Animals;
  };
  image.src = source;
};

const updateVisibility = () => {
  nodeGroup
    .selectAll("circle")
    .attr("opacity", (d) => (d._index < revealedCount ? 1 : 0))
    .attr("pointer-events", (d) => (d._index < revealedCount ? "auto" : "none"));

  nodeGroup
    .selectAll("text")
    .attr("opacity", (d) => (d._index < revealedCount ? 1 : 0));

  linkGroup.attr("opacity", (link) =>
    link.target._index < revealedCount ? 1 : 0
  );
};

const focusNode = (node) => {
  const scale = 1.4;
  const { width, height } = layoutSize;
  const translateX = width / 2 - node.y * scale;
  const translateY = height / 2 - node.x * scale;
  svg
    .transition()
    .duration(600)
    .call(
      zoomBehavior.transform,
      d3.zoomIdentity.translate(translateX, translateY).scale(scale)
    );
};

const showTooltip = (event, node) => {
  tooltip.innerHTML = `
    <strong>${node.data.name}</strong><br />
    <span>${node.data.mya} million years ago</span><br />
    <span>${node.data.detail}</span>
  `;
  tooltip.setAttribute("data-visible", "true");
  tooltip.setAttribute("aria-hidden", "false");
  moveTooltip(event);
};

const moveTooltip = (event) => {
  const offset = 16;
  const pageX = event.pageX || event.touches?.[0]?.pageX || 0;
  const pageY = event.pageY || event.touches?.[0]?.pageY || 0;
  tooltip.style.left = `${pageX + offset}px`;
  tooltip.style.top = `${pageY - offset}px`;
};

const hideTooltip = () => {
  tooltip.removeAttribute("data-visible");
  tooltip.setAttribute("aria-hidden", "true");
};

prevButton.addEventListener("click", () => {
  setActiveNode(activeIndex - 1, true);
});

nextButton.addEventListener("click", () => {
  if (activeIndex < revealedCount - 1) {
    setActiveNode(activeIndex + 1, true);
    return;
  }

  if (revealedCount < nodes.length) {
    revealedCount += 1;
    setActiveNode(revealedCount - 1, true);
  }
});

const resizeObserver = new ResizeObserver(() => init());
resizeObserver.observe(container);

init();
